Schema.voices.Add("Dispatch", "EVASION BEHAVIOR", "Lütfen dikkat, uyumsuz sanıkla tutarlı kaçınma davranışı. Yer koruma ekibi, uyarı, kod: İZOLE ET, AÇIĞA ÇIKAR, YÖNET.", "npc/overwatch/cityvoice/f_evasionbehavior_2_spkr.wav", true)
Schema.voices.Add("Dispatch", "INDIVIDUAL CHARGED WITH", "Birey, sosyo-tehlike seviyesiyle suçlanıyorsunuz: BİR. Koruma birimi kovuşturma kodu, GÖREV, KILIÇ, GECE YARISI.", "npc/overwatch/cityvoice/f_sociolevel1_4_spkr.wav", true)
