Schema.voices.Add("Dispatch", "DETECTION SYSTEMS DOWN", "Uyarı. Gözetim ve tespit sistemleri etkin değil. Kalan stabilizasyon ekibi üyeleri lütfen sınırlama durumunu bildirin.", "npc/overwatch/cityvoice/fprison_detectionsystemsout.wav", true)
